import React, { useState, useEffect } from 'react';
import './App.css';
import io from 'socket.io-client';

// Conectando-se ao servidor Socket.IO (mude o IP caso necess치rio)
const socket = io('http://localhost:3001'); // Substitua localhost pelo IP do servidor se necess치rio

function App() {
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  // Escutando as mensagens do servidor
  useEffect(() => {
    socket.on('receive-message', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    // Limpar o evento ao desmontar o componente
    return () => {
      socket.off('receive-message');
    };
  }, []);

  // Enviando uma nova mensagem
  const handleSendMessage = () => {
    if (message.trim() !== '') {
      const newMessage = { name, message };

      // Envia a mensagem para o servidor
      socket.emit('send-message', newMessage);

      setMessages([...messages, newMessage]);
      setMessage('');
    }
  };

  return (
    <div className="chat-container">
      {/* Header */}
      <div className="chat-header">
        <h1>Chat App</h1>
      </div>

      {/* Chat Body */}
      <div className="chat-body">
        {messages.map((msg, index) => (
          <div key={index} className="message">
            <span className="message-name">{msg.name}:</span>
            <span className="message-text">{msg.message}</span>
          </div>
        ))}
      </div>

      {/* Chat Input */}
      <div className="chat-footer">
        <input
          type="text"
          placeholder="Digite seu nome..."
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="input-name"
        />
        <input
          type="text"
          placeholder="Digite sua mensagem..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="input-message"
        />
        <button onClick={handleSendMessage} className="send-button">
          Enviar
        </button>
      </div>
    </div>
  );
}

export default App;
