import React, { useState } from 'react';
import './App.css';

function App() {
  const [input, setInput] = useState("");

  const handleClick = (value) => {
    setInput(input + value);
  };

  const handleClear = () => {
    setInput("");
  };

  const handleEquals = () => {
    try {
      setInput(eval(input).toString()); // Avalia a expressão
    } catch (error) {
      setInput("Erro");
    }
  };

  return (
    <div className="calculator">
      <h1>Calculadora</h1>
      <div className="display">{input || "0"}</div>
      <div className="buttons">
        <button onClick={handleClear} className="btn-clear">C</button>
        <button onClick={() => handleClick("/")} className="btn-operator">/</button>
        <button onClick={() => handleClick("*")} className="btn-operator">*</button>
        <button onClick={() => handleClick("-")} className="btn-operator">-</button>

        <button onClick={() => handleClick("7")}>7</button>
        <button onClick={() => handleClick("8")}>8</button>
        <button onClick={() => handleClick("9")}>9</button>
        <button onClick={() => handleClick("+")} className="btn-operator">+</button>

        <button onClick={() => handleClick("4")}>4</button>
        <button onClick={() => handleClick("5")}>5</button>
        <button onClick={() => handleClick("6")}>6</button>

        <button onClick={() => handleClick("1")}>1</button>
        <button onClick={() => handleClick("2")}>2</button>
        <button onClick={() => handleClick("3")}>3</button>
        <button onClick={handleEquals} className="btn-equals">=</button>

        <button onClick={() => handleClick("0")}>0</button>
        <button onClick={() => handleClick(".")}>.</button>
      </div>
    </div>
  );
}

export default App;
